import * as React from "react";
import base64 from "base-64";
import { View, Text, TextInput, StyleSheet, ScrollView } from "react-native";
import { Button } from "react-native-elements";
import AsyncStorage from '@react-native-async-storage/async-storage';

function Profile({ navigation }) {
  const [userinfo, setUserinfo] = React.useState({
    username: "",
    password: "",
  });

  const [inputList, setInputList] = React.useState([
    {
      title: "Personal Information",
      subList: [
        {
          title: "First Name",
          key: "firstName",
        },
        {
          title: "Last Name",
          key: "lastName",
        },
      ],
    },
    {
      title: "Fitness Goals",
      subList: [
        {
          title: "Daily Calories (kcal)",
          key: "dailyCalories",
        },
        {
          title: "Daily Protein (grams)",
          key: "dailyProtein",
        },
        {
          title: "Daily Carbs (grams)",
          key: "daily Carbs",
        },
        {
          title: "Daily Fat (grams)",
          key: "dailyFat",
        },
      ],
    },
  ]);

  React.useEffect(()=>{
    getInitInfo()
  },[])

  const getInitInfo =async ()=>{
    const token =await AsyncStorage.getItem('user_token')

    const username =await AsyncStorage.setItem('username')
    const reponse = await fetch("http://cs571.cs.wisc.edu:5000/users/"+username, {
      method: "GET",
      headers: {
        "x-access-token":token
      },
    });
  }

  //set value
  const inputChange = (e, key) => {
    setUserinfo((state) => {
      return {
        ...state,
        [key]: e,
      };
    });
  };

  //save
  const saveProfile = async () => {

  };

  const exitProfile = () => {
    navigation.navigate("Login");
  };


  return (
    <ScrollView>
      <View
        style={{
          flex: 1,
          alignItems: "center",
          justifyContent: "center",
          paddingBottom: 30,
          paddingTop: 20,
        }}
      >
        <Text style={styles.h1}>About Me</Text>
        <Text style={styles.paragraph}>Let's get to know you</Text>
        <Text style={[styles.paragraph, styles.bottomDistance]}>
          Specify your information below.
        </Text>

        {inputList.map((item) => {
          return (
            <View key={item.title} style={styles.bigWrapper}>
              <Text style={[styles.head, styles.bottomDistance]}>
                {item.title}
              </Text>
              {item.subList.map((subItem) => {
                return (
                  <View key={subItem.key} style={styles.bigWrapper}>
                    <Text style={styles.title}>{subItem.title}</Text>
                    <TextInput
                      value={userinfo.username}
                      style={styles.input}
                      placeholder={subItem.title}
                      placeholderTextColor="red"
                      onChangeText={(e) => {
                        inputChange(e, "username");
                      }}
                    />
                  </View>
                );
              })}
            </View>
          );
        })}

        <View style={styles.btnContainer}>
          <Button
            title="SAVE PROFILE"
            style={styles.save}
            onPress={saveProfile}
          ></Button>
          <Button title="EXIT" onPress={exitProfile}></Button>
        </View>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  h1: {
    fontSize: 26,
    fontWeight: "600",
    marginBottom: 30,
  },
  head: {
    fontSize: 20,
    fontWeight: "600",
    marginBottom: 20,
  },
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  bigWrapper: {
    alignItems: "center",
    justifyContent: "center",
  },

  input: {
    borderWidth: 1,
    borderColor: "red",
    width: 200,
    height: 35,
    marginBottom: 28,
    paddingLeft: 8,
  },
  btnContainer: {
    flexDirection: "row",
    width: 200,
    justifyContent: "space-around",
  },
  save: {
    marginRight: 12,
  },
  title: {
    fontSize: 14,
    marginBottom: 8,
    fontWeight: "600",
  },
  paragraph: {
    fontSize: 14,
    marginBottom: 8,
  },
  bottomDistance: {
    marginBottom: 30,
  },
});

export default Profile;
